function ifreturncheck(){
	var groupapp=false;
	var userapp=false;
	
	workflow.scratchpad.order = workflow.scratchpad.order+1;
	
	// Make sure wf_activity of the last approval is removed
	// to avoid the deletion of the record
	var sysid_current = current.sys_id.toString();
	
	var grApproval = new GlideRecord('sysapproval_approver');
	// Get the aproved approval in question (any where wf_activity is set)
	// We do not need to identify the "last" because all should have an empty entry in wf_activity
	grApproval.addEncodedQuery('state=approved^wf_activity!=NULL^sysapproval=' + sysid_current );
	grApproval.query();
	
	while (grApproval.next())
		{
		workflow.info('grApproval='+grApproval);
		grApproval.setValue('wf_activity','');
		grApproval.update();
	}
	
	
	var groupApproval = new GlideRecord('sysapproval_group');
groupApproval.addEncodedQuery('approval=approved^wf_activity!=NULL^parent='+ sysid_current);
groupApproval.query();

while (groupApproval.next())
	{
	groupApproval.setValue('wf_activity','');
	groupApproval.update();
}
	
	
	var orders = workflow.scratchpad.orders;
	
	
	if(workflow.scratchpad.order>workflow.scratchpad.ordercount) return 0;
	else return 1;
	}

